import { useContext } from "react";
import { CartContext } from "../context/CartContext";

const products = [
  { img: "https://tse1.mm.bing.net/th/id/OIP.2BnjyPdz3ipkkgsplIoSQAAAAA?cb=ucfimg2&ucfimg=1&w=300&h=420&rs=1&pid=ImgDetMain&o=7&rm=3", name: "Jogo - Crash Bandicoot", price: "R$120,00" },
  { img: "https://http2.mlstatic.com/D_NQ_NP_714142-CBT77180288538_072024-O.webp", name: "Camisa - Jujutsu Kaisen", price: "R$175,00" },
  { img: "https://cdn.media.amplience.net/s/hottopic/14118228_hi?h=630", name: "Action Figure - Son Goku", price: "R$150,00" },
  { img: "https://th.bing.com/th/id/OIP.PHZTkF7e1X48oaLcvtp_VAHaLo?w=199&h=313&c=7&r=0&o=7&cb=ucfimg2&pid=1.7&rm=3&ucfimg=1", name: "Manga - Chainsaw Man", price: "R$155,00" },
  { img: "https://static0.gamerantimages.com/wordpress/wp-content/uploads/2024/06/sonic-the-hedgehog-genesis.jpg", name: "Jogo - Sonic the Hegedhog", price: "R$159,00" },
  { img: "https://action-figure-district.de/wp-content/uploads/2014/04/stac-potter1.jpg", name: "Action Figure - Harry Potter", price: "R$29,00" },
  { img: "https://down-br.img.susercontent.com/file/br-11134201-22120-31nl9l7wiolv99", name: "Camisa - Berserk", price: "R$49,00" },
  { img: "https://images-na.ssl-images-amazon.com/images/I/81LQBd62YSL.jpg", name: "Manga - Beastar", price: "R$69,00" },
];

function Stats() {
  const { addToCart } = useContext(CartContext);

  return (
    <section className="products-section">
      <h2>CONFIRA NOSSAS NOVIDADES</h2>

      <div className="products-grid">
        {products.map((p, i) => (
          <div key={i} className="product-card">
            <img src={p.img} alt={p.name} />
            <h3>{p.name}</h3>
            <p>{p.price}</p>

            <button onClick={() => addToCart(p)}>
              Comprar
            </button>

          </div>
        ))}
      </div>
    </section>
  );
}

export default Stats;
